fun  Decode(str:String,s:BookSource):String{
  val jmkey="";
  val publicKey = 	KeyFactory.getInstance("RSA").generatePublic( X509EncodedKeySpec(Base64.decode(jmkey, 0)));

  val instance = Cipher.getInstance("RSA",BouncyCastleProvider());
  instance.init(2, publicKey);
  var decode = Base64.decode(str,0);
  val blockSize = 256

  var byteArrayOutputStream =  ByteArrayOutputStream(64);
  var i = 0;
  while (true) {
    var i2 = i * blockSize;
    if (decode.size - i2 > 0) {
      byteArrayOutputStream.write(instance.doFinal(decode, i2, blockSize));
      i++;
    } else {
      decode = byteArrayOutputStream.toByteArray();
      byteArrayOutputStream.close();
      return  String(decode);
    }
    }
}


