function getheader() {
	const {java,cookie,source} = this
    var u="";
    var ua=java.getWebViewUANEW();
    if(ua.match(/iPhone/i)){
        u=ua;
    }else{
        u="Mozilla/5.0 (Linux; U; Android 14; zh-cn; en-us; M2102K1AC Build/UKQ1.231207.002) AppleWebKit/537.36 (KHTML, like Gecko) Version/4.0 Chrome/109.0.5414.118 Mobile Safari/537.36 MQQBrowser/10.1.0"
    }
headers = {
	"User-Agent": u,
"Cookie":cookie.getCookie(source.key),
}
    return JSON.stringify(headers);
}
